mapper.select:
SELECT  id, bank_name, bank_note, bank_state, bank_tag, create_date, dir_type, excerpt_num,
        group_id1, group_id2, parent_id, qu_num, user_id, visibility
FROM t_question_bank


    AND bank_name = #{map.bankName}


    AND parent_id = #{map.parentId}


    AND id = #{map.id}


LIMIT 1

SELECT  id, bank_name, bank_note, bank_state, bank_tag, create_date, dir_type, excerpt_num,
group_id1, group_id2, parent_id, qu_num, user_id, visibility
FROM t_question_bank


    AND visibility = #{map.visibility}


    AND dir_type = #{map.dirType}


    AND bank_state = #{map.bankState}


    AND bank_tag = #{map.bankTag}


    AND group_id1 = #{map.groupId1}


    AND group_id2 = #{map.groupId2}


ORDER BY create_date DESC
